using System;
using Lacuna.Pki.Stores;
using System.Reflection;
using System.Web;
using Lacuna.Pki;

namespace WebApplication1.Service {
	public class Util {

		public static INonceStore GetNonceStore(IWebHostEnvironment hostingEnvironment) {
			return new FileSystemNonceStore(Path.Combine(hostingEnvironment.ContentRootPath, "App_Data"));
		}

		public static ITrustArbitrator GetTrustArbitrator() {

			var trustArbitrator = new LinkedTrustArbitrator(TrustArbitrators.PkiBrazil, TrustArbitrators.Windows);

			var lacunaRoot = Lacuna.Pki.PKCertificate.Decode(Convert.FromBase64String(""));
			trustArbitrator.Add(new TrustedRoots(lacunaRoot));

			var solutiTestRoot = Lacuna.Pki.PKCertificate.Decode(Convert.FromBase64String(""));
			trustArbitrator.Add(new TrustedRoots(solutiTestRoot));

			return trustArbitrator;
		}
	}
}

